// import { useState, useEffect } from "react";
// import axios from "axios";
// import { getCookie } from "../../helpers/auth";
import "./profile.scss";
const Profile = () => {
  // const [user, setUser] = useState({});
  // let getProfileUrl = "http://localhost:5000/api/auth/profile";
  // let getUserUrl = "http://localhost:5000/api/users/";
  // useEffect(() => {
  //   let token = getCookie("token");
  //   axios
  //     .get(getProfileUrl, {
  //       headers: {
  //         Authorization: token,
  //       },
  //     })
  //     .then((res) => res.data)
  //     .then((data) =>
  //       axios
  //         .get(getUserUrl + data.id)
  //         .then((res) => res.data)
  //         .then((data) => setUser(data))
  //     );
  // });
  return (
      <div className="profile">
        <div className="side-nav">
          <div className="pic">
            <img
              src="https://img-a.udemycdn.com/user/200_H/109959478_ce39.jpg?KGc8yYNyZCRjcb5jfrwU7HNpGegQyp2mt03n1uT4eXAkmYcqsJwFcssjIDRBLiczIDvSF2K7DbiEQlSb5a5JD9hEiobg0yG1X7jIbnUIeYHqyeexzXNcbtBBtCU"
              alt="profile"
            />
            <h2>Tunahan Aydinoglu</h2>
          </div>
          <div className="navi">
            <ul>
              <li>Profil</li>
              <li>Fotoğraf</li>
              <li>Hesabı Sil</li>
            </ul>
          </div>
        </div>
        <div className="form-panel">
          <h2>Profil Sayfası</h2>
          <h3>Profil bilgilerinizi buradan güncelleyebilirsiniz</h3>
          <form className="update-form">
            <div className="form-item">
              <label for="name">Ad Soyad</label>
              <input
                name="name"
                id="name"
                type="text"
                required
                placeholder="Ad Soyad"
              />
            </div>
            <div className="form-item">
              <label for="email">Email</label>
              <input
                name="email"
                id="email"
                type="email"
                required
                placeholder="example@mail.com"
              />
            </div>
            <div className="form-item">
              <label for="website">Web Siteniz</label>
              <input id="website" name="website" type="text" placeholder="" />
            </div>
            <div className="form-item">
              <label for="job">Meslek</label>
              <input id="job" name="job" type="text" placeholder="" />
            </div>
            <div className="form-item">
              <label for="place">Konum</label>
              <input name="place" id="place" type="text" placeholder="" />
            </div>
            <div className="form-item">
              <label for="about">Hakkında</label>
              <textarea
                name="about"
                id="about"
                type="text"
                required
                placeholder=""
              />
            </div>
            <div className="submit-section">
              <button>Kaydet</button>{" "}
            </div>
          </form>
        </div>
      </div>
  );
};
export default Profile;
