import { useState, useEffect } from "react";
import axios from "axios";
import { getCookie } from "../../helpers/auth";

const Profile = () => {
  const [user, setUser] = useState({});
  let getProfileUrl = "http://localhost:5000/api/auth/profile";
  let getUserUrl = "http://localhost:5000/api/users/";
  useEffect(() => {
    let id = "";
    let token = getCookie("token");
    axios
      .get(getProfileUrl, {
        headers: {
          Authorization: token,
        },
      })
      .then((res) => res.data)
      .then((data) =>
        axios
          .get(getUserUrl + data.id)
          .then((res) => res.data)
          .then((data) => setUser(data))
      );
  });
  return (
    <div>
      <ul>
        <li>{user.name}</li>
        <li>{user.email}</li>
        <li>{user.place}</li>
        <li>{user.title}</li>
      </ul>
    </div>
  );
};
export default Profile;
