import Axios from "axios";
import { useState } from "react";
import "./updateProfile.scss";
import { getCookie } from "../../helpers/auth";

export default function UpdateProfile(props) {
  const [NameUpdate, setNameUpdate] = useState("");
  const [EmailUpdate, setEmailUpdate] = useState("");
  const [WebUpdate, setWebUpdate] = useState("");
  const [JobUpdate, setJobUpdate] = useState("");
  const [AboutUpdate, setAboutUpdate] = useState("");
  const [PlaceUpdate, setPlaceUpdate] = useState("");

  const user = props.user;

  function handleSubmitUpdate(e) {
    let urlUpdate = "http://localhost:5000/api/auth/updateDetails";
    let token = getCookie("token");

    e.preventDefault();
    let updatedUser = {
      name: NameUpdate,
      email: EmailUpdate,
      website: WebUpdate,
      job: JobUpdate,
      place: PlaceUpdate,
      about: AboutUpdate,
    };

    Axios.put(urlUpdate, updatedUser, {
      headers: {
        Authorization: token,
      },
    });
  }

  return (
    <div className="updateProfile">
      <h2>Profil Sayfası</h2>
      <h3>Profil bilgilerinizi buradan güncelleyebilirsiniz</h3>
      <form className="update-form" onSubmit={handleSubmitUpdate}>
        <div className="items">
          <div className="form-item">
            <label htmlFor="name">Ad Soyad</label>
            <input
              name="name"
              id="name"
              type="text"
              required
              placeholder="Ad Soyad"
              value={user.name}
              onChange={(e) => setNameUpdate(e.target.value)}
            />
          </div>
          <div className="form-item">
            <label htmlFor="email">Email</label>
            <input
              name="email"
              id="email"
              type="email"
              required
              placeholder="example@mail.com"
              value={user.email}
              onChange={(e) => setEmailUpdate(e.target.value)}
            />
          </div>
          <div className="form-item">
            <label htmlFor="website">Web Siteniz</label>
            <input
              id="website"
              name="website"
              type="text"
              placeholder="www.ornek.com"
              value={user.website}
              onChange={(e) => setWebUpdate(e.target.value)}
            />
          </div>
          <div className="form-item">
            <label htmlFor="job">Meslek</label>
            <input
              id="job"
              name="job"
              type="text"
              placeholder=""
              value={user.job}
              onChange={(e) => setJobUpdate(e.target.value)}
            />
          </div>
          <div className="form-item">
            <label htmlFor="place">Konum</label>
            <input
              name="place"
              id="place"
              type="text"
              placeholder=""
              value={user.place}
              onChange={(e) => setPlaceUpdate(e.target.value)}
            />
          </div>
          <div className="form-item">
            <label htmlFor="about">Hakkında</label>
            <textarea
              name="about"
              id="about"
              type="text"
              placeholder="Kendinizden bahsetmek ister misiniz?"
              value={user.about}
              onChange={(e) => setAboutUpdate(e.target.value)}
            />
          </div>
        </div>
        <div className="submit-section">
          <button type="submit">Kaydet</button>
        </div>
      </form>
    </div>
  );
}
