import Axios from "axios";
import { useState } from "react";
import { getCookie } from "../../helpers/auth";
import fs from "fs";
import FormData from "form-data";
import "./updateImage.scss";

export default function UpdateImage(props) {
  // const [path, setPath] = useState('Geçerli formatlar ".jpg, .jpeg, .png"');
  let token = getCookie;
  const [image, setImage] = useState();
  const [degisken, setDegisken] = useState("");
  function handleSubmitImageUpdate(e) {
    e.preventDefault();
    var data = new FormData();
    data.append(
      "profile_image",
      image,
      "/C:/Users/clown/Downloads/default_profile.jpg"
    );

    fetch("http://localhost:5000/api/auth/upload", {
      method: "PUT",
      mode: "cors",
      headers: {
        Authorization: token,
      },
      body: JSON.stringify(data),
      redirect: "follow",
    })
      .then((response) => console.log(response))
      .catch((error) => console.log("error", error));
  }

  return (
    <div className="updateProfile">
      <h2>Fotoğraf</h2>
      <h3>Profil fotoğrafınızı bu alanda güncelleyebilirsiniz</h3>
      <form className="update-form" onSubmit={handleSubmitImageUpdate}>
        <div className="items">
          <div className="update-image">
            <label htmlFor="profile_image">
              <span> {degisken} </span>
              <span className="select-button">Görsel Seç</span>
            </label>
          </div>
          <input
            name="profile_image"
            id="profile_image"
            type="file"
            required
            onChange={(e) => {
              // setPath(e.target.value);
              let asd = e.target.value;
              setDegisken(asd.split("\\")[2]);
              setImage(e.target.files[0]);
            }}
          />
        </div>
        <div className="submit-section">
          <button type="submit">Profil Fotoğrafını Değiştir</button>
        </div>
      </form>
    </div>
  );
}
