import { useState, useEffect } from "react";
import axios from "axios";
import { getCookie } from "../../helpers/auth";

const Profile = () => {
  const [user, setUser] = useState({});
  let getProfileUrl = "http://localhost:5000/api/auth/profile";
  let getUserUrl = "http://localhost:5000/api/users/";
  useEffect(() => {
    let id = "";
    axios
      .get(getProfileUrl, {
        headers: {
          'Authorization': getCookie("token"),
        },
      })
      .then((res) => res.id)
      .then((data) => (id = data));
    axios.get(getUserUrl + id).then((res) => setUser(res));
  });
  return (
    <div>
      <ul>
        <li>{user.name}</li>
        <li>{user.email}</li>
        <li>{user.place}</li>
        <li>{user.title}</li>
      </ul>
    </div>
  );
};
export default Profile;
