import Axios from "axios";
import { useState } from "react";
import { getCookie } from "../../helpers/auth";
import fs from "fs";
import FormData from "form-data";
import "./updateImage.scss";

export default function UpdateImage(props) {
  // const [path, setPath] = useState('Geçerli formatlar ".jpg, .jpeg, .png"');
  let token = getCookie;
  const [image, setImage] = useState();
  const [degisken, setDegisken] = useState("");
  const  data = new FormData();
  function handleSubmitImageUpdate(e) {
    e.preventDefault();
    var data = new FormData();
    data.append('profile_image', fs.createReadStream('/C:\\Users\\clown\\Downloads\\default_profile.jpg'));
    
    var config = {
      method: 'put',
      url: 'http://localhost:5000/api/auth/upload',
      headers: { 
        'Authorization': token, 
        'Cookie': 'token='+token, 
        ...data.getHeaders()
      },
      data : data
    };
    
    Axios(config)
    .then(function (response) {
      console.log(JSON.stringify(response.data));
    })
    .catch(function (error) {
      console.log(error);
    });
  }
  function getBase64(e) {
    var file = e.target.files[0]
    let reader = new FileReader()
    reader.readAsDataURL(file)
    reader.onload = () => {
      this.setState({
        imgUpload: reader.result
      })
    };
    reader.onerror = function (error) {
      console.log('Error: ', error);
    }
  }
  return (
    <div className="updateProfile">
      <h2>Fotoğraf</h2>
      <h3>Profil fotoğrafınızı bu alanda güncelleyebilirsiniz</h3>
      <form className="update-form" onSubmit={handleSubmitImageUpdate}>
        <div className="items">
          <div className="update-image">
            <label htmlFor="profile_image">
              <span> {degisken} </span>
              <span className="select-button">Görsel Seç</span>
            </label>
          </div>
          <input
            name="profile_image"
            id="profile_image"
            type="file"
            required
            onChange={this.getBase64}
          />
        </div>
        <div className="submit-section">
          <button type="submit">Profil Fotoğrafını Değiştir</button>
        </div>
      </form>
    </div>
  );
}
