import { useState, useEffect } from "react";
import axios from "axios";
import { getCookie } from "../../helpers/auth";
import "./profile.scss";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import UpdateProfile from "./UpdateProfile";
const Profile = () => {
  const [user, setUser] = useState({});
  const [profile_image, setProfileImage] = useState("");
  useEffect(() => {
    let getProfileUrl = "http://localhost:5000/api/auth/user";
    let getUserUrl = "http://localhost:5000/api/users/profile/";
    let token = getCookie("token");
    axios
      .get(getProfileUrl, {
        headers: {
          Authorization: token,
        },
      })
      .then((res) => res.data.data)
      .then((data) =>
        axios
          .get(getUserUrl + data.id)
          .then((res) => res.data.data)
          .then((data) => setUser(data))
      );
    setProfileImage("http://localhost:5000/uploads/" + user.profile_image);
  });
  return (
    <Router>
      <div className="profile">
        <div className="wrapper">
          <div className="main">
            <div className="side-nav">
              <div className="pic">
                <img src={profile_image} alt="profile" />
                <h2>{user.name}</h2>
              </div>
              <div className="navi">
                <ul>
                  <li>Profil</li>
                  <li>Fotoğraf</li>
                  <li>Hesabı Sil</li>
                </ul>
              </div>
            </div>

            {/* <UpdateProfile  /> */}
            <Switch>
          <Route path="/updateImage">
            <Users />
          </Route>
          <Route path="/">
            <UpdateProfile user={user}/>
          </Route>
        </Switch>
          </div>
        </div>
      </div>
    </Router>
  );
};
export default Profile;
