import React, { useState } from "react";
import axios from "axios";

import * as Icons from "../icons/index";
import "./sign.scss";

const Sign = () => {
  const [emailLogin, setEmailLogin] = useState("");
  const [passwordLogin, setPasswordLogin] = useState("");

  let urlLogin = "http://localhost:5000/api/auth/login";
  const userLogin = {
    email:"",
    password:""
  };
  function handleSubmitLogin(event) {
    event.preventDefault();
    userLogin = {
      email: emailLogin,
      password: passwordLogin,
    };
    axios.post(urlLogin, { userLogin });
  }

  // function handleSubmitRegister(event) {
  //   event.preventDefault();
  //   console.log(email + password);
  // }
  return (
    <div className="popup">
      <div className="login">
        <form onSubmit={handleSubmitLogin}>
          <h1>Giriş Yap</h1>
          <div className="form-item">
            <span>
              <Icons.Email />
            </span>
            <input
              name="email"
              placeholder="E-mail"
              type="email"
              required="true"
              minLength="2"
              onChange={(e) => setEmailLogin(e.target.value)}
            />
          </div>
          <div className="form-item">
            <span>
              <Icons.Password />
            </span>
            <input
              name="password"
              placeholder="Şifre"
              type="password"
              minLength="7"
              required="true"
              maxLength="30"
              onChange={(e) => setPasswordLogin(e.target.value)}
            />
          </div>

          <button className="submit-button" type="submit">
            Giriş Yap
          </button>
        </form>
      </div>
      <div className="vertical-line"></div>
      <div className="register">
        <form>
          <h1>Kayit Ol</h1>
          <div className="form-item">
            <span>
              <Icons.ProfileFill />
            </span>
            <input
              name="name"
              placeholder="Ad Soyad"
              type="text"
              required="true"
              minLength="2"
            />
          </div>
          <div className="form-item">
            <span>
              <Icons.Email />
            </span>
            <input
              name="email"
              placeholder="E-mail"
              type="email"
              required="true"
              minLength="2"
            />
          </div>
          <div className="form-item">
            <span>
              <Icons.Password />
            </span>
            <input
              name="password"
              placeholder="Şifre"
              type="password"
              minLength="7"
              required="true"
              maxLength="30"
            />
          </div>

          <button className="submit-button" type="submit">
            Kayıt Ol
          </button>
        </form>
      </div>
    </div>
  );
};

export default Sign;
